SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_get_comment_proc ( nr_seq_comment_p cpoe_std_comment.nr_sequencia%type, nr_seq_proc_interno_p cpoe_procedimento.nr_seq_proc_interno%type) RETURNS varchar AS $body$
DECLARE

						
vl_retorno			varchar(20) := 'S';
is_show_comment_w	integer;
has_proc_w			integer;
nr_seq_order_type_w	cpoe_std_comment.nr_seq_order_type%type;
has_type_w			integer;
nr_seq_estrutura_w	integer;
has_structure_w		integer;WITH RECURSIVE cte AS (


c01 CURSOR FOR

SELECT				nr_sequencia
from				pi_estrutura WHERE nr_sequencia in (SELECT		nr_seq_estrutura
									from		cpoe_std_comment_proc
									where		nr_seq_std_comment = nr_seq_comment_p)
and		ie_situacao = 'A'
  UNION ALL


c01 CURSOR FOR

SELECT				nr_sequencia
from				pi_estrutura JOIN cte c ON (c.prior	nr_sequencia = nr_seq_estrut_sup)

) SELECT * FROM cte;
;

BEGIN

if (nr_seq_comment_p IS NOT NULL AND nr_seq_comment_p::text <> '' AND nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then

	select	nr_seq_order_type
	into STRICT	nr_seq_order_type_w
	from	cpoe_std_comment a
	where	a.nr_sequencia	=	nr_seq_comment_p;

	if (nr_seq_order_type_w IS NOT NULL AND nr_seq_order_type_w::text <> '') then
	
		select count(1)
		into STRICT	has_type_w
		from	proc_order_type
		where	nr_Seq_proc_interno =  nr_seq_proc_interno_p
		and 	nr_seq_order_type = nr_seq_order_type_w
		and		coalesce(cd_estabelecimento,1) = obter_estabelecimento_ativo;
		
			
		if (has_type_w > 0 ) then
			
			select	count(1)
			into STRICT	is_show_comment_w
			from	cpoe_std_comment_proc
			where	nr_seq_std_comment = nr_seq_comment_p;
			
			if (is_show_comment_w >= 1) then

				select	count(1)
				into STRICT	has_proc_w
				from	cpoe_std_comment_proc
				where	nr_seq_std_comment = nr_seq_comment_p
				and		nr_seq_proc_interno in (nr_seq_proc_interno_p);
	
				if (has_proc_w = 0) then
					
					select	count(1)
					into STRICT	nr_seq_estrutura_w
					from	cpoe_std_comment_proc
					where	nr_seq_std_comment = nr_seq_comment_p;
					
					if (nr_seq_estrutura_w >= 1) then
					
						for r_c01 in c01 loop
	
							begin
							
							select	count(1)
							into STRICT	has_structure_w
							from	pi_estrutura_cad
							where	nr_seq_estrutura = r_c01.nr_Sequencia
							and		nr_Seq_proc_int in (nr_Seq_proc_interno_p);
							
							
							exit when has_structure_w > 0;
									
							end;
							
							
						end loop;
						
						if (has_structure_w = 0) then
						
							vl_retorno := 'N';
							
						else
						
							vl_retorno := 'S';
							
						end if;
						
					else
					
						vl_retorno := 'N';
						
					end if;	
	
				else
				
					vl_retorno := 'S';
					
				end if;
				
			end if;
		
		else
		
			vl_retorno := 'N';
			
		end if;

	end if;
	
end if;

return vl_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
