CREATE OR REPLACE FUNCTION cpoe_get_dispens_criteria ( dt_reference_p timestamp, si_dispensation_day_p cpoe_week_dispens_rule.si_dispensation_day%type, hr_dispensation_time_p cpoe_week_dispens_rule.hr_dispensation_time%type, dt_inicio_material_p cpoe_material.dt_inicio%type) RETURNS varchar AS $body$
DECLARE


dt_disp_w		timestamp;
ie_dispensation_w	cpoe_material.si_dispensation_criteria%type := 'D';


BEGIN

if ((dt_reference_p IS NOT NULL AND dt_reference_p::text <> '') and (si_dispensation_day_p IS NOT NULL AND si_dispensation_day_p::text <> '') and (hr_dispensation_time_p IS NOT NULL AND hr_dispensation_time_p::text <> '')) then

	dt_disp_w := cpoe_prox_dt_disp_semanal(dt_reference_p, si_dispensation_day_p, hr_dispensation_time_p);
	
	if (dt_disp_w IS NOT NULL AND dt_disp_w::text <> '') then
	
		if (to_date(dt_inicio_material_p, 'dd/mm/yyyy hh:mi') >= to_date(dt_disp_w, 'dd/mm/yyyy hh:mi')) then
		
			ie_dispensation_w := 'S';
			
		elsif (to_date(dt_inicio_material_p, 'dd/mm/yyyy hh:mi') <= to_date(dt_disp_w, 'dd/mm/yyyy hh:mi')) then
		
			ie_dispensation_w := 'SD';
		
		end if;
	
	end if;

end if;

return ie_dispensation_w;

end;
$body$
