SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_get_instruct_restrict ( nr_seq_cpoe_material_p bigint, cd_material_p bigint, dt_inicio_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_instruction_item_w 	bigint;
qt_instruction_rp_w		bigint;
ds_return_w				varchar(255) := '';

c_instructions CURSOR FOR
SELECT	instruction.si_instruction_type	as	si_instruction_type,
		instruction.si_action			as	si_action
from 	mat_instruction_presc_rule	instruction
where 	instruction.cd_material = cd_material_p
    and ((instruction.dt_start < dt_inicio_p and coalesce(instruction.dt_end::text, '') = '')
	or (dt_inicio_p between instruction.dt_start and instruction.dt_end))
        and ((instruction.cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento_p::text, '') = ''));

BEGIN

  for r_instruction in c_instructions
  loop

    select 	count(1)
	into STRICT 	qt_instruction_item_w
    from 	cpoe_std_comment c,
			cpoe_comment_linkage b
	where	((nr_seq_cpoe_material_p = b.nr_seq_cpoe_material) or (nr_seq_cpoe_material_p = b.nr_seq_cpoe_material_tmp))
	and 	b.nr_seq_std_comment = c.nr_sequencia
	and 	c.si_instruction_type = r_instruction.si_instruction_type;

    select 	count(1)
	into STRICT 	qt_instruction_rp_w
	from	cpoe_std_comment c,
			cpoe_comment_linkage b,
			cpoe_material a
	where	((a.nr_seq_cpoe_rp = b.nr_seq_cpoe_rp) or (a.nr_seq_cpoe_rp = b.nr_seq_cpoe_rp_tmp))
	and		b.nr_seq_std_comment = c.nr_sequencia
	and		c.si_instruction_type = r_instruction.si_instruction_type
	and		a.nr_sequencia	= nr_seq_cpoe_material_p;

    if ((qt_instruction_item_w >= 1) or (qt_instruction_rp_w >= 1)) then
      ds_return_w := ds_return_w || r_instruction.si_instruction_type || ':' || r_instruction.si_action || ',';
    end if;
    
  end loop;

  return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
