SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_get_jahis_qrcode_part (nr_seq_order_unit_p bigint, si_part_p bigint) RETURNS text AS $body$
DECLARE


ds_retorno_w text;
x	bigint := 1;
i	bigint := 1;


BEGIN

	while(i <= si_part_p) loop
		if (i = si_part_p) then
			ds_retorno_w	:= substr(GET_JAHIS_QRCODE_INFO(nr_seq_order_unit_p),x,300);
		end if;
		x	:= x + 300;
		i := i + 1;
	end loop;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
