CREATE OR REPLACE FUNCTION cpoe_get_order_unit_date ( nr_seq_cpoe_order_unit_p bigint, nm_field_p text ) RETURNS timestamp AS $body$
DECLARE

	dt_retorno_w	timestamp := null;

BEGIN
begin
if (upper(nm_field_p) = 'DT_START') then
	select min(dt_inicio)
	into STRICT dt_retorno_w
	from cpoe_rp 
	where nr_seq_cpoe_order_unit = nr_seq_cpoe_order_unit_p;
elsif (upper(nm_field_p) = 'DT_END') then
	select max(dt_fim)
	into STRICT dt_retorno_w
	from cpoe_rp 
	where nr_seq_cpoe_order_unit = nr_seq_cpoe_order_unit_p;
end if;
exception
    when others then
    dt_retorno_w    := null;
end;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
