SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_get_order_unit_desc (nr_sequencia_p bigint, ie_type_out_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_type_out_p:
F = full
S = short
*/
ds_return_w varchar(2000);
nr_order_unit_w varchar(255);
ds_seq_cpoe_tipo_pedido_w varchar(255);
ds_type_of_prescription_w varchar(255);
nr_seq_classif_mat_ex_w varchar(255);
nr_seq_mat_lab_w varchar(255);
ds_classificacao_w varchar(255);
ds_material_exame_w varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select a.nr_order_unit,
	  b.ds_tipo ds_seq_cpoe_tipo_pedido,
	  obter_valor_dominio(10080, si_type_of_prescription) ds_type_of_prescription,
          a.nr_seq_classif_mat_ex,
          a.nr_seq_mat_lab
	into STRICT nr_order_unit_w,
	  ds_seq_cpoe_tipo_pedido_w,
	  ds_type_of_prescription_w,
          nr_seq_classif_mat_ex_w,
          nr_seq_mat_lab_w
	from cpoe_order_unit a, cpoe_tipo_pedido b
	where a.nr_sequencia = nr_sequencia_p
	and a.nr_seq_cpoe_tipo_pedido = b.nr_sequencia;


	if (ie_type_out_p = 'F') then

         ds_return_w := nr_order_unit_w || ' - ' || ds_seq_cpoe_tipo_pedido_w || ' - ' || ds_type_of_prescription_w;

         if (nr_seq_classif_mat_ex_w IS NOT NULL AND nr_seq_classif_mat_ex_w::text <> '') then

          select a.ds_classificacao
          into STRICT ds_classificacao_w
          from material_classif_exam_lab a
          where a.NR_SEQUENCIA = nr_seq_classif_mat_ex_w;

         end if;

         if (nr_seq_mat_lab_w IS NOT NULL AND nr_seq_mat_lab_w::text <> '') then

          select a.ds_material_exame
          into STRICT ds_material_exame_w
          from material_exame_lab a
          where a.NR_SEQUENCIA = nr_seq_mat_lab_w;

         end if;

         ds_return_w := nr_order_unit_w || ' - ' || ds_seq_cpoe_tipo_pedido_w || ' - ' || ds_type_of_prescription_w;

         if((nr_seq_classif_mat_ex_w IS NOT NULL AND nr_seq_classif_mat_ex_w::text <> '') or (nr_seq_mat_lab_w IS NOT NULL AND nr_seq_mat_lab_w::text <> '')) then

           ds_return_w := ds_return_w || '(';
    
           if (nr_seq_classif_mat_ex_w IS NOT NULL AND nr_seq_classif_mat_ex_w::text <> '') then 
            ds_return_w := ds_return_w || ds_classificacao_w;
           end if;

           if (nr_seq_mat_lab_w IS NOT NULL AND nr_seq_mat_lab_w::text <> '') then
            ds_return_w := ds_return_w || ' : ' || ds_material_exame_w;
           end if;

           ds_return_w := ds_return_w || ')';
        end if;
	elsif (ie_type_out_p = 'S') then
	  ds_return_w := nr_order_unit_w;
	end if;

end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
