CREATE OR REPLACE FUNCTION cpoe_get_prior_prescriber ( nr_seq_cpoe_previous_p bigint, nm_table_p text, ie_value_option_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_cpoe_previous_p IS NOT NULL AND nr_seq_cpoe_previous_p::text <> '' AND nm_table_p IS NOT NULL AND nm_table_p::text <> '') then
	if (ie_value_option_p = 'N') then
		ds_retorno_w := Obter_valor_Dinamico_char_bv('select obter_nome_medico(obter_pf_usuario(nvl(nm_usuario_nrec,nm_usuario),''C''),''PMS'') from ' || nm_table_p|| ' where nr_sequencia = :nr_sequencia', 'nr_sequencia=' || nr_seq_cpoe_previous_p || ';', ds_retorno_w);
	elsif (ie_value_option_p = 'D') then
		ds_retorno_w := Obter_valor_Dinamico_char_bv('select cpoe_get_approval_time(coalesce(dt_atualizacao_nrec, dt_inicio, sysdate)) from ' || nm_table_p|| ' where nr_sequencia = :nr_sequencia', 'nr_sequencia=' || nr_seq_cpoe_previous_p || ';', ds_retorno_w);
	end if;

end if;

return ds_retorno_w;

end;
$body$
