SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_get_proc_spec_approval ( nr_seq_proc_cpoe_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_user_certified_w		varchar(1);
si_allow_approve_w		varchar(2);
ie_aprov_esp_cpoe_w		proc_interno.ie_aprov_esp_cpoe%type;
dt_special_approval_w	timestamp;


BEGIN

if (nr_seq_proc_cpoe_p IS NOT NULL AND nr_seq_proc_cpoe_p::text <> '') then

	begin
		select	a.ie_aprov_esp_cpoe,
				b.dt_special_approval
		into STRICT	ie_aprov_esp_cpoe_w,
				dt_special_approval_w
		from	proc_interno a,
				cpoe_procedimento b
		where	b.nr_sequencia = nr_seq_proc_cpoe_p
		and		b.nr_seq_proc_interno = a.nr_sequencia;
	exception when others then
		ie_aprov_esp_cpoe_w		:= null;
		dt_special_approval_w 	:= null;
	end;
	
	if (ie_aprov_esp_cpoe_w = 'RAD') then
	
		si_allow_approve_w := obter_valor_param_usuario(2314, 33,  obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo);
		ie_user_certified_w := get_if_professional_certified(nm_usuario_p, '2', clock_timestamp());
		
		if ((ie_user_certified_w = 'Y' and si_allow_approve_w = 'UC') or (si_allow_approve_w  = 'S')) then
			return 'A';
		elsif (ie_user_certified_w = 'N') then
		
			if (coalesce(dt_special_approval_w::text, '') = '') then
				return 'PA';
			else
				return 'A';
			end if;
		end if;
	else
		return 'NA';
	end if;
	
end if;

return 'NA';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
