		nr_atendimento_p, cd_procedimento_p, ie_origem_proced_w, nr_seq_exame_p, nr_seq_proc_interno_p, cd_perfil_p, cd_setor_prescricao_p, cd_material_exame_p, nm_usuario_p, dt_prev_execucao_p, ds_erro_w, ds_mensagem_w, ie_abortar_w) INTO STRICT ds_erro_w, ds_mensagem_w, ie_abortar_w;

