CREATE OR REPLACE FUNCTION cpoe_get_type_nutrition_diet ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, meal_calculation_date_p timestamp, ie_tipo_dieta_p text) RETURNS varchar AS $body$
DECLARE

    ds_return_w varchar(3);

BEGIN
		if (ie_tipo_dieta_p = 'O') then
			select CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
			into STRICT   ds_return_w
			from   cpoe_dieta
			where  ie_tipo_dieta                               = ie_tipo_dieta_p
			and    ((nr_atendimento                            = nr_atendimento_p)
			or (cd_pessoa_fisica                           = cd_pessoa_fisica_p
			and    coalesce(nr_atendimento::text, '') = ''))
			and    (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
			and    ((coalesce(cpoe_obter_dt_suspensao(nr_sequencia,'N')::text, '') = '')
			or (cpoe_obter_dt_suspensao(nr_sequencia,'N')  >= meal_calculation_date_p))
			and (coalesce(dt_fim::text, '') = ''
			or     dt_fim                                      >= meal_calculation_date_p)
			and (dt_inicio                                  <= meal_calculation_date_p);
			
			if (ie_tipo_dieta_p = 'O' ) and (ie_tipo_dieta_p = 'E' )  then
				select CASE WHEN count(*)=0 THEN  'N'  ELSE 'SM' END
				into STRICT  ds_return_w
				from  cpoe_dieta
				where ((ie_tipo_dieta  in ('O','E')
				and   ie_dieta_especial = 'S')
				or    ie_tipo_dieta = 'E')			
				and   ((nr_atendimento                              = nr_atendimento_p)
				or (cd_pessoa_fisica                             = cd_pessoa_fisica_p
				and   coalesce(nr_atendimento::text, '') = ''))
				and   (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and   ((coalesce(cpoe_obter_dt_suspensao(nr_sequencia,'N')::text, '') = '')
				or (cpoe_obter_dt_suspensao(nr_sequencia,'N')   >= meal_calculation_date_p))
				and (coalesce(dt_fim::text, '') = ''
				or     dt_fim                                      >= meal_calculation_date_p)
				and (dt_inicio                                   <= meal_calculation_date_p);
			end if;
        end if;
        if (ie_tipo_dieta_p = 'J') then -- Overnight stay/Fasting(Out of hospital)
			select  coalesce(max('OO'),'OS')
			into STRICT    ds_return_w
			from    wocupacao_saida_temporaria w,
					atend_pac_un_log_mot_temp a
			where   a.nr_atendimento = nr_atendimento_p
			and     a.nr_atendimento = w.nr_atendimento
			and     (dt_movimentacao IS NOT NULL AND dt_movimentacao::text <> '') 
			and     (dt_est_return IS NOT NULL AND dt_est_return::text <> '')
			and     w.ie_sem_jejum = 'N'
			and     trunc(w.dt_inicio_saida)=trunc(a.dt_movimentacao);
		end if;
		if ( ds_return_w = 'S') then
         /* diet or oral diet*/

            ds_return_w := ie_tipo_dieta_p;
		elsif ( ds_return_w = 'OO')then
		 /* fasting or outof hospital*/

            ds_return_w := 'J';	
		else
		 /* Overnight Stay*/

		    ds_return_w := 'OS';	
        end if;
return ds_return_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
