SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_if_additional_text ( NR_SEQUENCIA_P bigint ) RETURNS varchar AS $body$
DECLARE

    SI_ADDITIONAL_TEXT varchar(1) := 'N';

BEGIN
    BEGIN
        SELECT
            CASE WHEN SI_ADDITIONAL_TEXT='Y' THEN  'Y'  ELSE 'N' END
        INTO STRICT SI_ADDITIONAL_TEXT
        FROM
            CPOE_STD_COMMENT       B,
            CPOE_COMMENT_LINKAGE   A
        WHERE
            A.NR_SEQ_STD_COMMENT = B.NR_SEQUENCIA
            AND A.NR_SEQUENCIA = NR_SEQUENCIA_P;

    EXCEPTION
        WHEN OTHERS THEN
            SI_ADDITIONAL_TEXT := 'N';
    END;

    RETURN SI_ADDITIONAL_TEXT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
