CREATE OR REPLACE FUNCTION cpoe_is_proc_order_type (nr_seq_proc_interno_p proc_order_type.nr_seq_proc_interno%type, nr_seq_order_unit_p proc_order_type.nr_seq_order_type%type, cd_estabelecimento_p proc_order_type.cd_estabelecimento%type ) RETURNS varchar AS $body$
DECLARE


 
 is_proc     varchar(1) := 'N';

 
BEGIN

 
     select  coalesce(max('S'),'N') 
     into STRICT    is_proc
 	from	cpoe_order_unit a,
 			PROC_ORDER_TYPE b
     where   a.nr_sequencia = nr_seq_order_unit_p
 	and		a.NR_SEQ_CPOE_TIPO_PEDIDO = b.nr_seq_order_type
 	and		b.NR_SEQ_PROC_INTERNO = nr_seq_proc_interno_p
     and (coalesce(b.cd_estabelecimento::text, '') = '' or b.cd_estabelecimento  = cd_estabelecimento_p)
 	and		b.IE_SITUACAO = 'A';

     return is_proc;

 end;

$body$
