CREATE OR REPLACE FUNCTION cpoe_nutrition_aditional_text ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE

    si_additional_text varchar(1) := 'I';

BEGIN
    BEGIN
        SELECT
            CASE WHEN ie_texto_adicional='A' THEN  'A'  ELSE 'I' END
        INTO STRICT si_additional_text
        FROM
            nut_orient_padrao_notas      b,
            nut_orient_pad_not_linkage   a
        WHERE
            a.nr_seq_orient_pad_not = b.nr_sequencia
            AND a.nr_sequencia = nr_sequencia_p;


    EXCEPTION
        WHEN OTHERS THEN
            si_additional_text := 'I';
            RAISE;
    END;

   RETURN si_additional_text;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
