SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obtain_data_pca_rel ( nr_sequencia_p cpoe_material.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

data_pca_w 		varchar(400);

--items
ie_tipo_analgesia_w		valor_dominio.ds_valor_dominio%type;
ie_pca_modo_prog_w		valor_dominio.ds_valor_dominio%type;
ie_bomba_w				valor_dominio.ds_valor_dominio%type;
qt_vol_infusao_pca_w	cpoe_material.qt_vol_infusao_pca%type;
ie_um_fluxo_pca_w		cpoe_material.ie_um_fluxo_pca%type;
qt_bolus_pca_w			cpoe_material.qt_bolus_pca%type;
ie_um_bolus_pca_w		cpoe_material.ie_um_bolus_pca%type;
qt_intervalo_bloqueio_w	cpoe_material.qt_intervalo_bloqueio%type;
qt_dose_inicial_pca_w	cpoe_material.qt_dose_inicial_pca%type;
ie_um_dose_inicio_pca_w	cpoe_material.ie_um_dose_inicio_pca%type;
qt_limite_quatro_hora_w cpoe_material.qt_limite_quatro_hora%type;
ie_um_limite_pca_w		cpoe_material.ie_um_limite_pca%type;
qt_limite_uma_hora_w	cpoe_material.qt_limite_uma_hora%type;
ie_um_limite_hora_pca_w	cpoe_material.ie_um_limite_hora_pca%type;
ie_tipo_solucao_w	cpoe_material.ie_tipo_solucao%type;


BEGIN
select  max(substr(obter_valor_dominio(2715,ie_tipo_analgesia),1,255)),
	max(substr(obter_valor_dominio(2714,ie_pca_modo_prog),1,255)),
	max(qt_vol_infusao_pca),
	max(ie_um_fluxo_pca),
	max(qt_bolus_pca),
	max(ie_um_bolus_pca),
	max(qt_intervalo_bloqueio),
	max(qt_dose_inicial_pca),
	max(ie_um_dose_inicio_pca),
	max(qt_limite_quatro_hora),
	max(ie_um_limite_pca),
	max(qt_limite_uma_hora),
	max(ie_um_limite_hora_pca),
	max(ie_bomba_infusao),
	max(ie_tipo_solucao)
into STRICT	ie_tipo_analgesia_w,
	ie_pca_modo_prog_w,
	qt_vol_infusao_pca_w,
	ie_um_fluxo_pca_w,
	qt_bolus_pca_w,
	ie_um_bolus_pca_w,
	qt_intervalo_bloqueio_w,
	qt_dose_inicial_pca_w,
	ie_um_dose_inicio_pca_w,
	qt_limite_quatro_hora_w,
	ie_um_limite_pca_w,
	qt_limite_uma_hora_w,
	ie_um_limite_hora_pca_w,
	ie_bomba_w,
	ie_tipo_solucao_w
from    cpoe_material
where   nr_sequencia = nr_sequencia_p;

if (ie_tipo_analgesia_w IS NOT NULL AND ie_tipo_analgesia_w::text <> '') then
	data_pca_w := substr(obter_desc_expressao( 299400,''),1,255) ||': '|| ie_tipo_analgesia_w ||'   ';
end if;

if (ie_pca_modo_prog_w IS NOT NULL AND ie_pca_modo_prog_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 293391,''),1,255) ||': '|| ie_pca_modo_prog_w ||'   ';
end if;

if (qt_vol_infusao_pca_w IS NOT NULL AND qt_vol_infusao_pca_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 290188,''),1,255) ||': '|| qt_vol_infusao_pca_w || ie_um_fluxo_pca_w ||'   ';
end if;

if (qt_bolus_pca_w IS NOT NULL AND qt_bolus_pca_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 284344,''),1,255) ||': '|| qt_bolus_pca_w || ie_um_bolus_pca_w ||'   ';
end if;

if (qt_intervalo_bloqueio_w IS NOT NULL AND qt_intervalo_bloqueio_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 292194,''),1,255) ||': '|| qt_intervalo_bloqueio_w ||'   ';
end if;

if (qt_dose_inicial_pca_w IS NOT NULL AND qt_dose_inicial_pca_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 288243,''),1,255) ||': '|| qt_dose_inicial_pca_w || ie_um_dose_inicio_pca_w ||'   ';
end if;

if (qt_limite_quatro_hora_w IS NOT NULL AND qt_limite_quatro_hora_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 292592,''),1,255) ||': '|| qt_limite_quatro_hora_w || ie_um_limite_pca_w ||'   ';
end if;

if (qt_limite_uma_hora_w IS NOT NULL AND qt_limite_uma_hora_w::text <> '') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 292590,''),1,255) ||': '|| qt_limite_uma_hora_w || ie_um_limite_hora_pca_w ||'   ';
end if;

if (coalesce(ie_tipo_solucao_w,'C') = 'P') and (coalesce(ie_bomba_w,'N') <> 'N') then
	data_pca_w := data_pca_w || substr(obter_desc_expressao( 288033,''),1,255) ||': '|| substr(obter_valor_dominio(1537,ie_bomba_w),1,255) ||'   ';
end if;

return	data_pca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cpoe_obtain_data_pca_rel ( nr_sequencia_p cpoe_material.nr_sequencia%type) FROM PUBLIC;

