CREATE OR REPLACE FUNCTION cpoe_obtain_desc_hemo_protocol ( cd_protocolo_p bigint, nr_seq_protocolo_p bigint, nr_seq_banco_sangue_p bigint) RETURNS varchar AS $body$
DECLARE


ds_derivado_w	san_derivado.ds_derivado%type;

ds_retorno_w	varchar(2000);

c01 CURSOR FOR
	SELECT	c.ds_derivado
	from	protocolo_medic_proc b,
			san_derivado c
	where	b.cd_protocolo = cd_protocolo_p
	and		b.nr_sequencia = nr_seq_protocolo_p
	and		b.nr_seq_solic_bs = nr_seq_banco_sangue_p
	and		b.nr_seq_derivado = c.nr_sequencia;

BEGIN

open c01;
loop
fetch c01 into ds_derivado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		ds_retorno_w := ds_retorno_w ||', '|| ds_derivado_w;
	else
		ds_retorno_w := ds_derivado_w;
	end if;
	end;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
