CREATE OR REPLACE FUNCTION cpoe_obtain_ref_calc (nr_sequence_p cpoe_material.nr_sequencia%type, ie_ref_calc_p cpoe_material.ie_ref_calculo%type) RETURNS varchar AS $body$
DECLARE

ds_return_w		varchar(50);

BEGIN
if (ie_ref_calc_p = 'NE') then--Stages
	select  max(nr_etapas)
	into STRICT	ds_return_w
	from	cpoe_material
	where 	nr_sequencia = nr_sequence_p;
elsif (ie_ref_calc_p = 'TE') then--Stage time
	select  max(qt_hora_fase)
	into STRICT	ds_return_w
	from	cpoe_material
	where 	nr_sequencia = nr_sequence_p;
elsif (ie_ref_calc_p = 'VT') then--Total volume
	select  max(qt_solucao_total)
	into STRICT	ds_return_w
	from	cpoe_material
	where 	nr_sequencia = nr_sequence_p;
elsif (ie_ref_calc_p = 'VI') then--Infusion rate
	select  max(qt_dosagem)||max(ie_tipo_dosagem)
	into STRICT	ds_return_w
	from	cpoe_material
	where 	nr_sequencia = nr_sequence_p;
else 							--Interval
	select  max(obter_desc_intervalo(cd_intervalo))
	into STRICT	ds_return_w
	from	cpoe_material
	where 	nr_sequencia = nr_sequence_p;
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
