CREATE OR REPLACE FUNCTION cpoe_obtain_time_application ( qt_old_dose_p cpoe_dieta.qt_dose%type, qt_new_dose_p cpoe_dieta.qt_dose%type, qt_tempo_aplic_p cpoe_dieta.qt_tempo_aplic%type, qt_vel_infusao_p cpoe_dieta.qt_vel_infusao%type ) RETURNS varchar AS $body$
DECLARE


	qt_minutos_w	double precision;
	ds_return_w		varchar(50) := null;


BEGIN
	if ((coalesce(qt_old_dose_p, 0) > 0) and (coalesce(qt_new_dose_p, 0) > 0) and (qt_tempo_aplic_p IS NOT NULL AND qt_tempo_aplic_p::text <> '')) then
		qt_minutos_w := obter_minutos_hora(qt_tempo_aplic_p);

		if (qt_minutos_w IS NOT NULL AND qt_minutos_w::text <> '') then
			qt_minutos_w := (qt_new_dose_p * qt_minutos_w) / qt_old_dose_p;

			ds_return_w := substr(pls_formatar_minutos(qt_minutos_w), 1, 5);
		end if;
	end if;

	return ds_return_w;

end;
$body$
