CREATE OR REPLACE FUNCTION cpoe_obter_alta_medica ( ie_baixado_por_alta_p text default null, dt_alta_medico_p timestamp default null, dt_suspensao_p timestamp default null) RETURNS varchar AS $body$
DECLARE

ie_alta_medica_w varchar(1);

BEGIN
if ((ie_baixado_por_alta_p = 'S') and (coalesce(dt_alta_medico_p, clock_timestamp()) <= clock_timestamp()) and (coalesce(dt_suspensao_p::text, '') = '')) then
	ie_alta_medica_w := 'S';
else
	ie_alta_medica_w := 'N';
end if;

return ie_alta_medica_w;

end;
$body$
