CREATE OR REPLACE FUNCTION cpoe_obter_comp_med_prot (cd_protocolo_p bigint, nr_seq_protocolo_p bigint, nr_seq_material_p bigint, nr_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_componente_w		varchar(100);
ds_descricao_w		varchar(2000);
ds_plus_w		varchar(1);

c01 CURSOR FOR
SELECT	substr(obter_desc_material(cd_material),1,100)
from	protocolo_medic_material
where	cd_protocolo	= cd_protocolo_p
and	nr_sequencia	= nr_seq_protocolo_p
and	nr_seq_material	<> nr_seq_material_p
and	nr_agrupamento	= nr_agrupamento_p
and	ie_agrupador	= 1
order by nr_seq_material LIMIT 7;


BEGIN
if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

	select	substr(obter_desc_material(cd_material),1,100)
	into STRICT	ds_descricao_w
	from	protocolo_medic_material
	where	cd_protocolo	= cd_protocolo_p
	and	nr_sequencia	= nr_seq_protocolo_p
	and	nr_seq_material	= nr_seq_material_p
	and	ie_agrupador	= 1;

	ds_plus_w := wheb_mensagem_pck.get_texto(1027282);

	open C01;
	loop
	fetch C01 into
		ds_componente_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_descricao_w := ds_descricao_w || ' ' || ds_plus_w || ' ' || ds_componente_w;
		end;
	end loop;
	close C01;

end if;

return substr(ds_descricao_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
