CREATE OR REPLACE FUNCTION cpoe_obter_count_revalidacao (nr_atendimento_p bigint, nr_seq_revalidation_rule_p cpoe_revalidation_rule.nr_sequencia%type default null, nm_usuario_p text default null) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint := 0;
qt_hors_copia_w		bigint := 0;


BEGIN

qt_hors_copia_w 	:= get_qt_hours_after_copy_cpoe(obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo);

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	sum(qt_itens)
	into STRICT	qt_retorno_w
	from (
		SELECT     count(1) qt_itens 
		from	cpoe_dialise   
		where nr_atendimento = nr_atendimento_p  
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
		and	cd_funcao_origem = 2314 
		and cpoe_obter_data_prox_reval(nr_sequencia, 'D') < clock_timestamp() 
        and cpoe_obter_se_prescritor_reval(nr_seq_revalidation_rule_p, ie_motivo_prescricao, cd_perfil_ativo, nm_usuario_nrec) = 'S'
		and coalesce(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE Obter_Data_Menor(dt_suspensao,dt_fim) END , clock_timestamp() + interval '1 days') > (clock_timestamp() + (1/24))
		and (dt_prox_geracao IS NOT NULL AND dt_prox_geracao::text <> '')
		and	((cpoe_obter_se_copia_item_term(ds_horarios,
											null,
											dt_prox_geracao + (qt_hors_copia_w/24),
											CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao, dt_fim) END ) = 'S')
			or ((coalesce(dt_fim::text, '') = '') and ie_duracao = 'C'))			
		
union all

		SELECT     count(1) qt_itens
		from	cpoe_gasoterapia 
		where nr_atendimento = nr_atendimento_p  
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
		and	cd_funcao_origem = 2314 
		and cpoe_obter_data_prox_reval(nr_sequencia, 'G') < clock_timestamp() 
        and cpoe_obter_se_prescritor_reval(nr_seq_revalidation_rule_p, ie_motivo_prescricao, cd_perfil_ativo, nm_usuario_nrec) = 'S'
		and coalesce(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE Obter_Data_Menor(dt_suspensao,dt_fim) END , clock_timestamp() + interval '1 days') > (clock_timestamp() + (1/24)) 
		and (dt_prox_geracao IS NOT NULL AND dt_prox_geracao::text <> '')
		and	((cpoe_obter_se_copia_item_term(ds_horarios,
											cd_intervalo,
											dt_prox_geracao + (qt_hors_copia_w/24),
											CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao, dt_fim) END ) = 'S')
			or ((coalesce(dt_fim::text, '') = '') and ie_duracao = 'C'))
		
union all

		select     count(1) qt_itens
		from cpoe_material 
		where nr_atendimento = nr_atendimento_p  
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
		and	cd_funcao_origem = 2314 
		and  coalesce(ie_material, 'N') = 'N' 
		and cpoe_obter_data_prox_reval(nr_sequencia, 'M') < clock_timestamp()
        and cpoe_obter_se_prescritor_reval(nr_seq_revalidation_rule_p, ie_motivo_prescricao, cd_perfil_ativo, nm_usuario_nrec) = 'S'
		and coalesce(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih,dt_fim)  ELSE Obter_Data_Menor(dt_suspensao,dt_fim) END , clock_timestamp() + interval '1 days') > (clock_timestamp() + (1/24)) 
		and (dt_prox_geracao IS NOT NULL AND dt_prox_geracao::text <> '')
		and	((cpoe_obter_se_copia_item_term(ds_horarios,
											cd_intervalo,
											dt_prox_geracao + (qt_hors_copia_w/24),
											CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih, dt_fim)  ELSE coalesce(dt_suspensao, dt_fim) END ) = 'S')
			or ((coalesce(dt_fim::text, '') = '') and ie_duracao = 'C'))	
		
union all

		select     count(1) qt_itens
		from	cpoe_recomendacao 
		where nr_atendimento = nr_atendimento_p  
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
		and	cd_funcao_origem = 2314 
		and cpoe_obter_data_prox_reval(nr_sequencia, 'R') < clock_timestamp() 
        and cpoe_obter_se_prescritor_reval(nr_seq_revalidation_rule_p, ie_motivo_prescricao, cd_perfil_ativo, nm_usuario_nrec) = 'S'
		and coalesce(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE Obter_Data_Menor(dt_suspensao,dt_fim) END , clock_timestamp() + interval '1 days') > (clock_timestamp() + (1/24)) 
		and (dt_prox_geracao IS NOT NULL AND dt_prox_geracao::text <> '')
		and	((cpoe_obter_se_copia_item_term(ds_horarios,
											cd_intervalo,
											dt_prox_geracao + (qt_hors_copia_w/24),
											CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao, dt_fim) END ) = 'S')
			or ((coalesce(dt_fim::text, '') = '') and ie_duracao = 'C'))
		
union all

		select     count(1) qt_itens
		from	cpoe_procedimento 
		where nr_atendimento = nr_atendimento_p  
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
		and	cd_funcao_origem = 2314 
		and cpoe_obter_data_prox_reval(nr_sequencia, 'P') < clock_timestamp()
        and cpoe_obter_se_prescritor_reval(nr_seq_revalidation_rule_p, ie_motivo_prescricao, cd_perfil_ativo, nm_usuario_nrec) = 'S'
		and coalesce(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE Obter_Data_Menor(dt_suspensao,dt_fim) END , clock_timestamp() + interval '1 days') > (clock_timestamp() + (1/24)) 
		and (dt_prox_geracao IS NOT NULL AND dt_prox_geracao::text <> '')
		and	((cpoe_obter_se_copia_item_term(ds_horarios,
											cd_intervalo,
											dt_prox_geracao + (qt_hors_copia_w/24),
											CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao, dt_fim) END ) = 'S')
			or ((coalesce(dt_fim::text, '') = '') and ie_duracao = 'C'))
		
union all
	
		select     count(1) qt_itens
		from	cpoe_dieta
		where nr_atendimento = nr_atendimento_p  
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
		and	cd_funcao_origem = 2314 
		and cpoe_obter_data_prox_reval(nr_sequencia, 'N') < clock_timestamp()
        and cpoe_obter_se_prescritor_reval(nr_seq_revalidation_rule_p, ie_motivo_prescricao, cd_perfil_ativo, nm_usuario_nrec) = 'S'
		and coalesce(CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE Obter_Data_Menor(dt_suspensao,dt_fim) END , clock_timestamp() + interval '1 days') > (clock_timestamp() + (1/24))
		and (dt_prox_geracao IS NOT NULL AND dt_prox_geracao::text <> '')
		and	((cpoe_obter_se_copia_item_term(ds_horarios,
											cd_intervalo,
											dt_prox_geracao + (qt_hors_copia_w/24),
											CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao, dt_fim) END ) = 'S')
			or ((coalesce(dt_fim::text, '') = '') and ie_duracao = 'C'))) alias137;
end if;

return qt_retorno_w;

end;
$body$
