CREATE OR REPLACE FUNCTION cpoe_obter_dados_perit_rel ( ie_tipo_peritoneal_p text, nr_ciclos_p bigint, qt_volume_ciclo_p bigint, qt_hora_min_duracao_p text, qt_volume_total_di_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);

	function ciclos return text is
	;
BEGIN
		return ' '|| obter_desc_expressao(284898, null) || ': ' || nr_ciclos_p || '															';
	end;

	function volume_ciclo return varchar2 is
	begin
		return obter_desc_expressao(302247, null) || ': '|| qt_volume_ciclo_p || '															' || obter_desc_expressao(284893, null) || ': '|| qt_hora_min_duracao_p;
	end;

	function volume_total return varchar2 is
	begin
		return ' ' || obter_desc_expressao(302288, null) || ': ' || nr_ciclos_p || '';
	end;

begin
--Inserido espaços pois futuramente relatório wate que usa isto se tornará um cate onde será melhorado isso.
	if (ie_tipo_peritoneal_p <> 'DPI') then
		ds_retorno_w := ciclos();
	end if;

	ds_retorno_w := ds_retorno_w || volume_ciclo();

	if (ie_tipo_peritoneal_p <> 'DPI') then
		ds_retorno_w := ds_retorno_w || '															' || volume_total();
	end if;

	return	ds_retorno_w;

end;
