SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_dados_rel_incons ( nr_sequencia_p bigint, ie_tipo_item_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_item_w		varchar(255);
ds_retorno_w	varchar(255);
dt_inicio_w		timestamp;
/*
IE_OPCAO_P
D - Descricao item CPOE.
I - Data de inicio
*/
BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_tipo_item_p in ('M','MAT','SOL')) then
		select	max(substr(obter_desc_material(cd_material),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_material
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_tipo_item_p = 'P') then
		select	max(substr(Obter_Desc_Proc_Interno(nr_seq_proc_interno),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_procedimento
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_tipo_item_p = 'O') then
		select	max(substr(obter_nome_dieta(cd_dieta),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_dieta
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_item_p in ('E', 'S')) then
		select	max(substr(obter_desc_material(cd_material),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_dieta
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_item_p = 'J') then
		select	max(substr(obter_desc_tipo_jejum(nr_seq_tipo),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_dieta
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_item_p = 'DI') then
		select	max(substr(cpoe_obter_dialise_rel(ie_tipo_dialise, ie_hemodialise,ie_tipo_peritoneal),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_dialise
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_tipo_item_p = 'L') then
		select	max(substr(obter_desc_material(cd_mat_prod1),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_dieta
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_item_p = 'G') then
		select	max(substr(Obter_Desc_Gas(nr_seq_gas),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w	,
			dt_inicio_w
		from	cpoe_gasoterapia
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_tipo_item_p = 'R') then
		select	max(substr(obter_desc_recomendacao(cd_recomendacao),1,255)),
			max(dt_inicio)
		into STRICT	ds_item_w,
			dt_inicio_w
		from	cpoe_recomendacao
		where	nr_sequencia	= nr_sequencia_p;
	end if;

end if;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_item_w;
elsif (ie_opcao_p = 'I' and (dt_inicio_w IS NOT NULL AND dt_inicio_w::text <> '')) then
	ds_retorno_w := to_char(dt_inicio_w, 'dd/mm/yyyy hh24:mi');
end if;

return ds_retorno_w;

exception when others then
	return '';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
