CREATE OR REPLACE FUNCTION cpoe_obter_data_exec_exame ( nr_sequencia_p bigint, nr_seq_agenda_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_horario_w			agenda_paciente.hr_inicio%type;
ie_status_agenda_w		agenda_paciente.ie_status_agenda%type;
nr_seq_agenda_w			prescr_medica.nr_seq_agenda%type;


BEGIN

select	coalesce(max(a.nr_seq_agenda), nr_seq_agenda_p)
into STRICT	nr_seq_agenda_w
from	prescr_medica a,
	prescr_procedimento b
where	a.nr_prescricao = b.nr_prescricao
and	b.nr_seq_proc_cpoe = nr_sequencia_p;

if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
	select	max(hr_inicio),
		max(ie_status_agenda)
	into STRICT	dt_horario_w,
		ie_status_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_w;

	return dt_horario_w;
end if;

return	null;

end;
