CREATE OR REPLACE FUNCTION cpoe_obter_data_inicio_item ( dt_inicio_p timestamp, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


dt_horario_retorno_w	timestamp;

BEGIN

dt_horario_retorno_w := dt_inicio_p;

if (obter_operacao_intervalo(cd_intervalo_p) = 'D') then
	dt_horario_retorno_w := obter_proxima_dose_medic(dt_inicio_p, cd_intervalo_p, null, 'N');
end if;

return to_char(dt_horario_retorno_w,'dd/mm/yyyy hh24:mi:ss');

end;
