CREATE OR REPLACE FUNCTION cpoe_obter_data_maior (dt_suspensao_p timestamp, dt_fim_p timestamp) RETURNS timestamp AS $body$
BEGIN

if (coalesce(dt_suspensao_p::text, '') = '') and (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') then
		return dt_fim_p;

elsif (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') and (coalesce(dt_fim_p::text, '') = '') then
		return dt_suspensao_p;

elsif (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') and (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then

	if (dt_fim_p > dt_suspensao_p) then
		return dt_fim_p;
	else
		return dt_suspensao_p;
	end if;
else
	return dt_fim_p;
end if;

return coalesce(dt_fim_p,clock_timestamp());

end;
$body$
