SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_data_prescr ( nr_sequencia_p bigint, ie_tipo_item_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


/*
ie_opcao_p
F - DT_LIBERACAO_FARMACIA
LE - DT_LIBERACAO_ENFERMAGEM
DR - DT_REVISAO
DA - DT_INICIO_ANALISE_FARM
DE - DT_INICIO_ANALISE_ENF
*/
				
ds_retorno_w			timestamp;
nr_prescricao_w			prescr_medica.nr_prescricao%type;
dt_liberacao_farmacia_w		prescr_medica.dt_liberacao_farmacia%type;
dt_liberacao_enfermagem_w	prescr_medica.dt_liberacao%type;
dt_revisao_w			prescr_medica.dt_revisao%type;
dt_inicio_analise_farm_w	prescr_medica.dt_inicio_analise_farm%type;



BEGIN

nr_prescricao_w	:= obter_prescr_item_cpoe(nr_sequencia_p,ie_tipo_item_p);

if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
	select	max(dt_liberacao_farmacia),
		max(dt_liberacao),
		max(dt_revisao),
		max(dt_inicio_analise_farm)
	into STRICT	dt_liberacao_farmacia_w,
		dt_liberacao_enfermagem_w,
		dt_revisao_w,
		dt_inicio_analise_farm_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_w;
	
	if (ie_opcao_p	= 'F') then
		ds_retorno_w	:= dt_liberacao_farmacia_w;
	elsif (ie_opcao_p	= 'LE') then
		ds_retorno_w	:= dt_liberacao_enfermagem_w;
	elsif (ie_opcao_p	= 'DR') then
		ds_retorno_w	:= dt_revisao_w;
	elsif (ie_opcao_p	= 'DA') then
		ds_retorno_w	:= dt_inicio_analise_farm_w;
	elsif (ie_opcao_p	= 'DE') then
		select	max(dt_inicio_analise_enf)
		into STRICT	ds_retorno_w
		from	prescr_medica_compl
		where	nr_prescricao = nr_prescricao_w;
	end if;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
