CREATE OR REPLACE FUNCTION cpoe_obter_data_prox_reval ( nr_seq_cpoe_p bigint, ie_tipo_item_p text, ie_horas_anterior_p text default 'S', dt_liberacao_p timestamp default null) RETURNS timestamp AS $body$
DECLARE


dt_validacao_w				timestamp := clock_timestamp() - interval '1 days';
qt_horas_anterior_w			cpoe_revalidation_rule.qt_horas_anterior%type := 0;
nr_seq_revalidation_rule_w 	cpoe_revalidation_rule.nr_sequencia%type;


BEGIN

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '' AND ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then

	if (ie_tipo_item_p = 'N') then
	
		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_diet = nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p = 'H') then
		
		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_hemotherapy = nr_seq_cpoe_p;
	
	elsif (ie_tipo_item_p = 'D' or ie_tipo_item_p = 'DI' or ie_tipo_item_p = 'DP') then
	
		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_dialysis = nr_seq_cpoe_p;

	elsif (ie_tipo_item_p = 'G') then
	
		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_gasotherapy = nr_seq_cpoe_p;
	
	elsif (ie_tipo_item_p = 'M' or ie_tipo_item_p = 'MA') then
	
		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_material = nr_seq_cpoe_p;

	elsif (ie_tipo_item_p = 'R') then

		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_recomendation = nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p = 'P') then

		select	coalesce(max(dt_validacao),obter_excecao_regra_reval(nr_seq_cpoe_p,ie_tipo_item_p,dt_liberacao_p)),
				max(nr_seq_revalidation_rule)
		into STRICT	dt_validacao_w,
				nr_seq_revalidation_rule_w
		from 	cpoe_revalidation_events
		where	nr_seq_exam = nr_seq_cpoe_p;

	end if;

	if (nr_seq_revalidation_rule_w IS NOT NULL AND nr_seq_revalidation_rule_w::text <> '' AND dt_validacao_w IS NOT NULL AND dt_validacao_w::text <> '') then
		if (ie_horas_anterior_p = 'S') then
			select	coalesce(max(qt_horas_anterior), 0)
			into STRICT	qt_horas_anterior_w
			from	cpoe_revalidation_rule
			where	nr_sequencia = nr_seq_revalidation_rule_w
			and 	coalesce(ie_situacao, 'A') = 'A';
		end if;
		dt_validacao_w := dt_validacao_w - (qt_horas_anterior_w/24);
	end if;
end if;

return coalesce(dt_validacao_w, clock_timestamp() - interval '1 days');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
