CREATE OR REPLACE FUNCTION cpoe_obter_data_receita_amb ( nr_seq_receita_amb_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


/*
ie_opcao_p
I - Retorna data de in√≠cio da receita
F - Retorna data de fim da receita
*/
dt_retorno_w			timestamp := clock_timestamp();
dt_inicio_receita_w		timestamp;
dt_fim_receita_w		timestamp;


BEGIN

if (nr_seq_receita_amb_p IS NOT NULL AND nr_seq_receita_amb_p::text <> '') then

	select 	dt_receita,
			dt_validade_receita
	into STRICT	dt_inicio_receita_w,
			dt_fim_receita_w
	from	fa_receita_farmacia
	where	nr_sequencia = nr_seq_receita_amb_p;

	if (ie_opcao_p = 'I') then
		dt_retorno_w := dt_inicio_receita_w;
	end if;

	if (ie_opcao_p = 'F') then
		dt_retorno_w := dt_fim_receita_w;
	end if;

end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
