CREATE OR REPLACE FUNCTION cpoe_obter_desc_derivado ( nr_seq_cpoe_hemo_p cpoe_hemoterapia.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w	varchar(255);

BEGIN
 
select	substr(max(b.ds_derivado),1,255) 
into STRICT	ds_retorno_w 
from	cpoe_hemoterapia a, 
		san_derivado b 
where	a.nr_seq_derivado = b.nr_sequencia 
and		a.nr_sequencia = nr_seq_cpoe_hemo_p;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
