SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_desc_estrutura_proc ( nr_seq_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(200);
ds_tipo_estrutura_w varchar(100);
ds_estrutura_w varchar(100);
nr_seq_est_proc_w bigint;
nr_seq_est_proc_sup_w bigint;
nr_seq_est_w bigint;
nr_seq_proc_w bigint;


BEGIN

select max(c.NR_SEQ_ESTRUTURA)
into STRICT nr_seq_est_w
from cpoe_procedimento pr 
inner join PI_ESTRUTURA_CAD c on c.NR_SEQUENCIA = pr.NR_SEQ_PROC_ESTRUTURA
where pr.NR_SEQUENCIA = nr_seq_proc_p;

if (nr_seq_est_w IS NOT NULL AND nr_seq_est_w::text <> '') then
    nr_seq_est_proc_w := nr_seq_est_w;
end if;
if (nr_seq_est_proc_w IS NOT NULL AND nr_seq_est_proc_w::text <> '') then

    select max(NR_SEQ_ESTRUT_SUP) into STRICT nr_seq_est_proc_sup_w 
	from PI_ESTRUTURA 
	where NR_SEQUENCIA = nr_seq_est_proc_w;
	if (nr_seq_est_proc_sup_w IS NOT NULL AND nr_seq_est_proc_sup_w::text <> '') then
		  nr_seq_est_proc_w := nr_seq_est_proc_sup_w;
	end if;

    select max(t.DS_TIPO_ESTRUTURA)
    into STRICT ds_tipo_estrutura_w
    from PI_TIPO_ESTRUTURA t
    inner join PI_ESTRUTURA p on p.NR_SEQ_TIPO = t.NR_SEQUENCIA
    where p.NR_SEQUENCIA = nr_seq_est_proc_w;

    if (ds_tipo_estrutura_w IS NOT NULL AND ds_tipo_estrutura_w::text <> '') then
        ds_retorno_w := ds_tipo_estrutura_w;
    end if;

    select max(e.DS_ESTRUTURA) 
    into STRICT ds_estrutura_w
    from PI_ESTRUTURA e
    where e.NR_SEQUENCIA = nr_seq_est_proc_w;

    if (coalesce(ds_tipo_estrutura_w::text, '') = '' and (ds_estrutura_w IS NOT NULL AND ds_estrutura_w::text <> '')) then 
        ds_retorno_w := ds_estrutura_w;
    end if;
    if (ds_tipo_estrutura_w IS NOT NULL AND ds_tipo_estrutura_w::text <> '' AND ds_estrutura_w IS NOT NULL AND ds_estrutura_w::text <> '') then
        ds_retorno_w := ds_retorno_w || ' ' || ds_estrutura_w;
    end if;
end if;

return  ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
