CREATE OR REPLACE FUNCTION cpoe_obter_desc_info_hemote ( nr_sequencia_p cpoe_hemoterapia.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(255);


BEGIN
select 	max(ds_derivado)
into STRICT	retorno_w
from   	san_derivado
where  	nr_sequencia = (SELECT nr_seq_derivado
	   				  from	  cpoe_hemoterapia
					  where	  nr_sequencia = nr_sequencia_p);

if (coalesce(retorno_w::text, '') = '') then
	select 	substr(obter_desc_prescr_proc(cd_procedimento,ie_origem_proced, nr_seq_proc_interno),1,255)
	into STRICT	retorno_w
	from	cpoe_hemoterapia
	where	nr_sequencia = nr_sequencia_p;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
