CREATE OR REPLACE FUNCTION cpoe_obter_desc_proced_assoc (nr_seq_proced_cpoe_p cpoe_procedimento.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE
					
  ds_retorno_w	procedimento.ds_procedimento%type := ' ';							

BEGIN

if ((nr_seq_proced_cpoe_p IS NOT NULL AND nr_seq_proced_cpoe_p::text <> '') and nr_seq_proced_cpoe_p > 0) then
  select	max(substr(cpoe_obter_desc_proc_interno(nr_seq_proc_interno),1,255))
  into STRICT	ds_retorno_w
  from	cpoe_procedimento
  where	nr_sequencia = nr_seq_proced_cpoe_p;
end if;

return ds_retorno_w;

end;
$body$
