CREATE OR REPLACE FUNCTION cpoe_obter_desc_unid_med_gas (ie_unidade_medida_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);


BEGIN

ds_retorno_w := '';

	if (ie_unidade_medida_p = 'gtm') then
