	elsif (ie_unidade_medida_p = 'MCG/KG/MIN') then
		ds_retorno_w := obter_desc_expressao(781877);--'mcg/kg/min';
	elsif (ie_unidade_medida_p = 'MCG/MIN') then
		ds_retorno_w := obter_desc_expressao(650637);--'mcg/min';
	elsif (ie_unidade_medida_p = 'MCG/ML') then --'mcg/ml plasma'
		ds_retorno_w := obter_desc_expressao(782140);
	elsif (ie_unidade_medida_p = 'MG/H') then
		ds_retorno_w := obter_desc_expressao(782134);--'mg/hora';
	elsif (ie_unidade_medida_p = 'MG/KG/H') then
