	elsif (ie_unidade_medida_p = 'MG/KG/MIN') then
		ds_retorno_w := obter_desc_expressao(782152);--'mg/kg/min';
	elsif (ie_unidade_medida_p = 'mgm') then
		ds_retorno_w := obter_desc_expressao(491619);--'mcgts/min';
	elsif (ie_unidade_medida_p = 'mlh') then
		ds_retorno_w := obter_desc_expressao(782130);--'ml/hora';
	elsif (ie_unidade_medida_p = 'NG/ML') then
		ds_retorno_w := obter_desc_expressao(782136);--'ng/ml plasma';
	elsif (ie_unidade_medida_p = 'PERC') then
		ds_retorno_w := '%';
	elsif (ie_unidade_medida_p = 'PPM') then
		ds_retorno_w := 'ppm';
	else
		ds_retorno_w := '';
	end if;

return	ds_retorno_w;

