CREATE OR REPLACE FUNCTION cpoe_obter_dialise_comp_rel ( ie_tipo_dialise_p text, qt_fluxo_sangue_p bigint, qt_hora_min_sessao_p text, ie_tipo_peritoneal_p text, ie_hemodialise_p text, ie_categoria_p text, nr_seq_mod_dialisador_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(4000);
ie_altera_label_campo_w		varchar(1);
ds_dialisador_w				varchar(80);

	function fluxo_sangue return text is
	;
BEGIN
			if (ie_categoria_p = 'C') and (coalesce(ie_altera_label_campo_w,'N') = 'S') then
				return ' ' || obter_desc_expressao(290194, null) || ': '|| qt_fluxo_sangue_p ||' ';
			else
				return ' ' || obter_desc_expressao(665643, null) || ': '|| qt_fluxo_sangue_p ||' ';
			end if;

		return ' ' || obter_desc_expressao(665643, null) || ': '|| qt_fluxo_sangue_p ||' ';
	end;

	function hora_min_sessao return varchar2 is
	begin
		return ' ' || obter_desc_expressao(289010, null) || ': '|| qt_hora_min_sessao_p ||' ';
	end;

	function tipo_peritoneal return varchar2 is
	begin
		if (ie_tipo_peritoneal_p = 'CAPD') then
			return ' ' || obter_desc_expressao(689353, null) || '													';
		elsif (ie_tipo_peritoneal_p = 'DPA') then
			return ' ' || obter_desc_expressao(689355, null) || '													';
		elsif (ie_tipo_peritoneal_p = 'DPI') then
			return ' ' || obter_desc_expressao(292175, null) || '													';
		end if;

		return null;
	end;

	function modelo_dialisador return varchar2 is
	begin

		if (nr_seq_mod_dialisador_p IS NOT NULL AND nr_seq_mod_dialisador_p::text <> '') then
			select	max(ds_modelo)
			into STRICT	ds_dialisador_w
			from	hd_modelo_dialisador
			where	nr_sequencia = nr_seq_mod_dialisador_p;

			if (ds_dialisador_w IS NOT NULL AND ds_dialisador_w::text <> '') then
				return obter_desc_expressao(293388, null) || ': ' || ds_dialisador_w;
			end if;
		end if;

		return null;

	end;

begin
--Inserido espaços pois futuramente relatório wate que usa isto se tornará um cate onde será melhorado isso.
ie_altera_label_campo_w := Obter_Param_Usuario(924, 1171, obter_perfil_ativo, Obter_Usuario_Ativo, obter_estabelecimento_ativo, ie_altera_label_campo_w);

	if (ie_tipo_dialise_p = 'DI') then
		ds_retorno_w := fluxo_sangue || '      ' ||  hora_min_sessao || '      ' || modelo_dialisador;
	elsif (ie_tipo_dialise_p = 'DP') then
		ds_retorno_w := tipo_peritoneal;
	end if;

	return	ds_retorno_w;

end;
$body$
