SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_dias_semana_disp (cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ds_dias_semana_w	varchar(30);
ie_segunda_w		intervalo_prescricao.ie_segunda%type;
ie_terca_w			intervalo_prescricao.ie_terca%type;
ie_quarta_w			intervalo_prescricao.ie_quarta%type;
ie_quinta_w			intervalo_prescricao.ie_quinta%type;
ie_sexta_w			intervalo_prescricao.ie_sexta%type;
ie_sabado_w			intervalo_prescricao.ie_sabado%type;
ie_domingo_w		intervalo_prescricao.ie_domingo%type;
ie_operacao_w		intervalo_prescricao.ie_operacao%type;


BEGIN
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	select	max(ie_segunda),
		max(ie_terca),
		max(ie_quarta),
		max(ie_quinta),
		max(ie_sexta),
		max(ie_sabado),
		max(ie_domingo),
		max(ie_operacao)
	into STRICT	ie_segunda_w,
		ie_terca_w,
		ie_quarta_w,
		ie_quinta_w,
		ie_sexta_w,
		ie_sabado_w,
		ie_domingo_w,
		ie_operacao_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_operacao = 'D';

	--pkg_date_utils.get_WeekDay
	if (ie_domingo_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w  || '0 ';
	end if;

	if (ie_segunda_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || '1 ';
	end if;

	if (ie_terca_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || '2 ';
	end if;

	if (ie_quarta_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || '3 ';
	end if;

	if (ie_quinta_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || '4 ';
	end if;

	if (ie_sexta_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || '5 ';
	end if;

	if (ie_sabado_w = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || '6 ';
	end if;


end if;

return trim(both ds_dias_semana_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
