CREATE OR REPLACE FUNCTION cpoe_obter_dia_util_atb ( nr_sequencia_p bigint, cd_material_p bigint, nr_dia_util_p bigint, dt_inicio_p timestamp ) RETURNS bigint AS $body$
DECLARE

				
ds_retorno_w			cpoe_material.nr_dia_util%type;
ie_dias_util_medic_w	material.ie_dias_util_medic%type;
dt_inicio_medic_w		prescr_material.dt_inicio_medic%type;
dt_inicio_base_w		timestamp;


BEGIN
begin

	ds_retorno_w := nr_dia_util_p;

	select 	max(ie_dias_util_medic)
	into STRICT 	ie_dias_util_medic_w
	from 	material
	where	cd_material = cd_material_p;

	select 	max(dt_inicio_medic)
	into STRICT 	dt_inicio_medic_w
	from 	prescr_material
	where 	nr_seq_mat_cpoe = nr_sequencia_p
	and 	cd_material = cd_material_p;
	
	if (ie_dias_util_medic_w = 'O' and nr_dia_util_p > 0) or (ie_dias_util_medic_w = 'S' and nr_dia_util_p > 1) then
	
		dt_inicio_base_w := ESTABLISHMENT_TIMEZONE_UTILS.todayAtTime(to_char(dt_inicio_p,'hh24:mi:ss'));
		
		if (dt_inicio_medic_w > trunc(dt_inicio_base_w + 1,'mi') - 1/86400) and (dt_inicio_medic_w not between dt_inicio_base_w and (trunc(dt_inicio_base_w + 1,'mi') - 1/86400)) then
			
			ds_retorno_w := nr_dia_util_p - 1;
		end if;	
	end if;

return	ds_retorno_w;

exception
when others then
	return nr_dia_util_p;
end;

end;
$body$
LANGUAGE PLPGSQL
