CREATE OR REPLACE FUNCTION cpoe_obter_dose_medic_pac ( cd_pessoa_fisica_p text, cd_material_p bigint, cd_unidade_medida_p text) RETURNS bigint AS $body$
DECLARE


qt_dose_retorno_w		prescr_material.qt_dose%type;


BEGIN
select	coalesce(sum(obter_dose_convertida(a.cd_material, a.qt_dose, a.cd_unidade_medida, cd_unidade_medida_p)),0)
into STRICT	qt_dose_retorno_w
from	cpoe_pac_dose_material a
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
and		a.cd_material		= cd_material_p;

return qt_dose_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
