CREATE OR REPLACE FUNCTION cpoe_obter_ds_dado_clinico (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dado_clinico_w		varchar(255):='';


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	substr(coalesce(max(ds_dado_clinico),''),1,255)
	into STRICT	ds_dado_clinico_w
	from	cpoe_procedimento
	where 	nr_seq_lista_espera = nr_sequencia_p;
end if;

return	ds_dado_clinico_w;

end;
