CREATE OR REPLACE FUNCTION cpoe_obter_ds_dieta_rel ( ie_tipo_dieta_p text, nr_seq_mat_dieta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);


BEGIN

if (ie_tipo_dieta_p = 'O') then
	ds_retorno_w	:= substr(obter_nome_dieta(nr_seq_mat_dieta_p),1,255);
elsif (ie_tipo_dieta_p = 'S' or ie_tipo_dieta_p = 'E') then
	ds_retorno_w	:= substr(obter_desc_material(nr_seq_mat_dieta_p),1,255);
elsif (ie_tipo_dieta_p = 'L') then
	ds_retorno_w	:= obter_desc_material(nr_seq_mat_dieta_p);
elsif (ie_tipo_dieta_p = 'J') then
	select   max(ds_tipo)
	into STRICT     ds_retorno_w
	from     rep_tipo_jejum
	where    nr_sequencia = nr_seq_mat_dieta_p;
elsif (ie_tipo_dieta_p = 'P')then --nutrição parenteral
	select	max(ds_expressao)
	into STRICT	ds_retorno_w
	from	valor_dominio_v
	where	vl_dominio = 'P'
	and		cd_dominio = 1880;
elsif (ie_tipo_dieta_p = 'I') then --nutrição parenteral infantil
	select	max(ds_expressao)
	into STRICT	ds_retorno_w
	from	valor_dominio_v
	where	vl_dominio = 'I'
	and		cd_dominio = 1880;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
