CREATE OR REPLACE FUNCTION cpoe_obter_ds_estrutura_proc (separator_p text, nr_seq_nivel_atual_p pi_estrutura.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w                  varchar(4000);
level_name_w                  pi_estrutura.ds_estrutura%type;
level_root_sequence_w         pi_estrutura.nr_seq_estrut_sup%type;


BEGIN
    select
            pe.ds_estrutura as ds_level_name,
            pe.nr_seq_estrut_sup as level_root_sequence 
    into STRICT 
            level_name_w ,
            level_root_sequence_w
    from     
            pi_estrutura pe
    where  pe.nr_sequencia = nr_seq_nivel_atual_p;

    ds_retorno_w :=  level_name_w;
    if (level_root_sequence_w IS NOT NULL AND level_root_sequence_w::text <> '')
    then
        ds_retorno_w := cpoe_obter_ds_estrutura_proc(separator_p, level_root_sequence_w) || separator_p || ds_retorno_w;
    end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
