CREATE OR REPLACE FUNCTION cpoe_obter_ds_incons (ie_tipo_incons_p text, ds_incons_aux_p text) RETURNS varchar AS $body$
DECLARE

ds_incons_aux_w		varchar(4000);

BEGIN

