CREATE OR REPLACE FUNCTION cpoe_obter_dt_fim_etapa_gas ( dt_horario_p timestamp, ds_duracao_p text, nr_ocorrencia_p bigint, ie_modo_adm_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;
dt_prim_horario_w		timestamp;
dt_pausa_w				timestamp;
qt_horas_w				double precision;
qt_horas_duracao_w		double precision := 0;
nr_horas_w				varchar(3);
nr_minutos_w			varchar(3);

BEGIN

if (ds_duracao_p IS NOT NULL AND ds_duracao_p::text <> '') and (position(':' in ds_duracao_p) > 0) and (ds_duracao_p <> '  :  ') and (ds_duracao_p <> '__:__') then
	nr_horas_w			:= substr(ds_duracao_p, 1, position(':' in ds_duracao_p)-1);
	nr_minutos_w		:= substr(ds_duracao_p,position(':' in ds_duracao_p) + 1, length(ds_duracao_p));
	qt_horas_duracao_w 	:= ((nr_horas_w)::numeric  + dividir_sem_round((coalesce((nr_minutos_w)::numeric ,0))::numeric, 60));
else
	qt_horas_duracao_w := 24;
end if;

if (ie_modo_adm_p = 'C') then
	qt_horas_w := dividir_sem_round(qt_horas_duracao_w, coalesce(nr_ocorrencia_p,1));
	dt_retorno_w := dt_horario_p + dividir_sem_round((qt_horas_w)::numeric, 24);
else
	dt_retorno_w := dt_horario_p + dividir_sem_round((qt_horas_duracao_w)::numeric, 24);
end if;

return dt_retorno_w;

end;
