SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_dt_inicio (nr_seq_cpoe_p bigint, ie_tipo_item_p text) RETURNS timestamp AS $body$
DECLARE


dt_inicio_w	timestamp;


BEGIN

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then

	if (ie_tipo_item_p in ('D', 'J', 'SNE', 'LD', 'S', 'NPTA', 'NPTI')) then
		
		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_dieta
		where	nr_sequencia = nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p = 'HM') then
		
		select	max(coalesce(dt_inicio, dt_programada))
		into STRICT	dt_inicio_w
		from 	cpoe_hemoterapia
		where	nr_sequencia = nr_seq_cpoe_p;

	elsif (ie_tipo_item_p in ('D','DI','DP')) then
		
		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_dialise
		where	nr_sequencia	= nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p = 'O') then

		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_gasoterapia
		where	nr_sequencia	= nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p in ('M', 'MAT', 'SOL')) then
	
		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_material
		where	nr_sequencia	= nr_seq_cpoe_p;

	elsif (ie_tipo_item_p = 'R') then
	
		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_recomendacao
		where	nr_sequencia	= nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p = 'P') then
	
		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_procedimento
		where	nr_sequencia	= nr_seq_cpoe_p;
		
	elsif (ie_tipo_item_p = 'AP') then
	
		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	cpoe_anatomia_patologica
		where	nr_sequencia	= nr_seq_cpoe_p;
			
	end if;

end if;
		
return dt_inicio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
