SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_etapas_formatadas ( ds_horarios_p text, cd_intervalo_p text, qt_hora_fase_p text) RETURNS varchar AS $body$
DECLARE

	ds_horarios_old_w		varchar(4000);
	ds_horarios_new_w		varchar(4000);
	ds_hora_aux_w			varchar(5);
	index_corte_w			integer;
	ds_etapa_w				varchar(30);
	qt_hora_w			bigint;

BEGIN
	ds_horarios_new_w		:= trim(both ds_horarios_p) || ' ';

	-- das #@NR_PRIM_HOR#@ Ã s
	ds_etapa_w	:= wheb_mensagem_pck.get_texto(307054);

	ds_horarios_old_w	:= ds_horarios_new_w;
	ds_horarios_new_w	:= '';

	index_corte_w		:= position(' ' in ds_horarios_old_w);

	if (coalesce(qt_hora_fase_p::text, '') = '') then
		qt_hora_w := Obter_ocorrencia_intervalo(cd_intervalo_p,24,'H');
	else
		qt_hora_w := obter_minutos_hora(qt_hora_fase_p)/60;
	end if;

	while(index_corte_w > 0) loop
		ds_hora_aux_w		:= substr(ds_horarios_old_w,1,index_corte_w-1);
		ds_horarios_old_w	:= substr(ds_horarios_old_w,index_corte_w+1);

		if (position(':' in ds_hora_aux_w) = 0) then
			ds_hora_aux_w	:= ds_hora_aux_w || ':00';
		end if;

		ds_horarios_new_w := ds_horarios_new_w || replace(ds_etapa_w,'#@NR_PRIM_HOR#@',ds_hora_aux_w) || ' ';

		ds_horarios_new_w := ds_horarios_new_w || to_char(to_date(ds_hora_aux_w,'hh24:mi') + (qt_hora_w/24), 'hh24:mi') || '; ';

		index_corte_w		:= position(' ' in ds_horarios_old_w);

	end loop;

	return ds_horarios_new_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
