CREATE OR REPLACE FUNCTION cpoe_obter_exame_nao_checado (nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


c01_w                   cpoe_procedimento%rowtype;
nr_seq_proc_cpoe_w      cpoe_procedimento.nr_sequencia%type;
ie_exame_nao_checado_w  varchar(1);

C01 CURSOR FOR
SELECT	nr_sequencia
from	cpoe_procedimento
where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	    ((coalesce(cpoe_obter_dt_suspensao(nr_sequencia,'N')::text, '') = '') or (cpoe_obter_dt_suspensao(nr_sequencia,'N') >= clock_timestamp()))
and (coalesce(dt_fim::text, '') = '' or to_char(dt_fim, 'dd/mm/yyyy') >= to_char(clock_timestamp(), 'dd/mm/yyyy'))
and	    to_char(dt_inicio, 'dd/mm/yyyy') <= to_char(clock_timestamp(), 'dd/mm/yyyy');

C02 CURSOR FOR
SELECT  b.nr_prescricao,
        b.nr_sequencia
from    prescr_procedimento b,
        prescr_proc_hor c
where   b.nr_seq_proc_cpoe = nr_seq_proc_cpoe_w
and     b.nr_sequencia = c.nr_seq_procedimento
and     (b.nr_prescricao IS NOT NULL AND b.nr_prescricao::text <> '')
and     (c.dt_lib_horario IS NOT NULL AND c.dt_lib_horario::text <> '')
and     c.nr_prescricao = b.nr_prescricao;

BEGIN

<<loop_items_cpoe>>
for c01_w in C01 loop
    nr_seq_proc_cpoe_w := c01_w.nr_sequencia;
    for c02_w in C02 loop
        select  CASE WHEN count(0)=0 THEN  'S'  ELSE 'N' END 
        into STRICT    ie_exame_nao_checado_w
        from    prescr_mat_alteracao d
        where   d.nr_seq_procedimento = c02_w.nr_sequencia
        and     d.nr_prescricao = c02_w.nr_prescricao
        and     d.ie_alteracao = 3;

        if (ie_exame_nao_checado_w = 'S') then 
            exit loop_items_cpoe;
        end if;
    end loop;
end loop loop_items_cpoe;

return coalesce(ie_exame_nao_checado_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
