CREATE OR REPLACE FUNCTION cpoe_obter_formato_display ( ds_valor_p text, ie_formato_p text ) RETURNS varchar AS $body$
DECLARE

    ds_resultado  varchar(2000);
    nr_tam        bigint;

BEGIN
    ds_resultado := ds_valor_p;
    IF (ie_formato_p IS NOT NULL AND ie_formato_p::text <> '') THEN
        IF ie_formato_p = '0' THEN
            ds_resultado :=   regexp_replace(ds_valor_p,'([[:digit:]]{4})([[:digit:]]{2})([[:digit:]]{2})','\1/\2/\3');
        ELSIF ie_formato_p = '1' THEN
            ds_resultado := regexp_replace(ds_valor_p,'([[:digit:]]{2})([[:digit:]]{2})([[:digit:]]{2})','\1:\2:\3');
        ELSIF ie_formato_p = '2' THEN
             ds_resultado := regexp_replace(ds_valor_p,'([[:digit:]]{2})([[:digit:]]{2})','\1:\2');
        ELSIF ie_formato_p = '3' THEN
            nr_tam := length(ds_resultado);
            ds_resultado := to_char((substr(ds_resultado, 1, nr_tam - 3))::numeric  ||'.'||
                                               substr(ds_resultado, nr_tam - 2));

        END IF;

    END IF;

    RETURN ds_resultado;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
