CREATE OR REPLACE FUNCTION cpoe_obter_hemo_rel ( ie_tipo_hemoterap_p text, nr_seq_derivado_p bigint, cd_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);

	function hemocomponente return text is
		ds_retorno_ww	varchar(255);
	
BEGIN

		select	ds_derivado ds
		into STRICT	ds_retorno_ww
		from	san_derivado
		where	nr_sequencia = nr_seq_derivado_p;

		return ds_retorno_ww;
	end;

begin

if (ie_tipo_hemoterap_p = '0') then
	ds_retorno_w := hemocomponente();
elsif (ie_tipo_hemoterap_p = '1') then
	ds_retorno_w := obter_desc_procedimento(cd_procedimento_p, '');
end if;

return	ds_retorno_w;

end;
$body$
