SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cpoe_obter_info_suspensao (nr_sequencia_p bigint, ie_tipo_item_p text, ie_informacao_out_p text) RETURNS varchar AS $body$
DECLARE

 
dt_suspensao_w		cpoe_material.dt_suspensao%type;				
nm_usuario_susp_w	usuario.ds_usuario%type;

/* 
ie_informacao_out_p 
D = Data suspensão 
N = nome usuário suspensão 
*/
 
 
 

BEGIN 
 
if (coalesce(nr_sequencia_p,0) > 0) then 
	 
	if (ie_tipo_item_p = 'N') then 
		 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_dieta 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;
	 
	elsif (ie_tipo_item_p = 'M') then 
	 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_material 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;	
	 
	elsif (ie_tipo_item_p = 'P') then 
 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_procedimento 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;	
		 
	elsif (ie_tipo_item_p = 'G') then 
	 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_gasoterapia 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;		
	 
	elsif (ie_tipo_item_p = 'R') then 
 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_recomendacao 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;		
	 
	elsif (ie_tipo_item_p = 'D') then 
	 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_dialise 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;			
	 
	elsif (ie_tipo_item_p = 'H') then 
	 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_hemoterapia 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;			
	 
	elsif (ie_tipo_item_p = 'I') then 
	 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_intervencao 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;
		 
	elsif (ie_tipo_item_p = 'AP') then 
	 
		select	max(dt_suspensao), 
				max(obter_nome_usuario(nm_usuario_susp)) 
		into STRICT	dt_suspensao_w, 
				nm_usuario_susp_w 
		from	cpoe_anatomia_patologica 
		where	(dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') 
		and nr_sequencia = nr_sequencia_p;	
		 
	end if;
end if;
 
if (ie_informacao_out_p = 'D') then 
	return	coalesce(to_char(dt_suspensao_w,'dd/mm/yyyy hh24:mi:ss'),null);
else 
	return	coalesce(nm_usuario_susp_w,null);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cpoe_obter_info_suspensao (nr_sequencia_p bigint, ie_tipo_item_p text, ie_informacao_out_p text) FROM PUBLIC;

